import axios from "axios"
import { LISTS_LIST_FAIL, LISTS_LIST_REQUEST, LISTS_LIST_SUCCESS } from "../constants/listsConstants"

export const listLists = () => async (dispatch, getState) => {
    try {
        dispatch({
            type: LISTS_LIST_REQUEST,
        });
        const {
            userLogin: {userInfo},

        } = getState();

        const config = {
            headers: {
                Authorization: `Bearer ${userInfo.token}`,
            },

        };

        const {data} = await axios.get(`/api/lists`, config);

        dispatch({
            type: LISTS_LIST_SUCCESS,
            payload: data
        });



    } catch (error) {
        const message = 
        error.response && error.response.data.message
        ? error.response.data.message
        : error.message;
        dispatch({
            type: LISTS_LIST_FAIL,
            payload: message,
        });
    }
}
